# File input

input = ''
list_of_letters = []
for letter in input:
    list_of_letters.append(letter)

# Task 01

i = 4

while i < len(list_of_letters):
    group_four = list_of_letters[i - 4:i]
    if len(set(group_four)) == len(group_four):
        print(i)
        break
    else:
        i += 1

